'use strict';

/**
* Update a license header in each file in a provided file list.
*
* @module @stdlib/_tools/licenses/update-header-file-list
*
* @example
* var updateHeader = require( '@stdlib/_tools/licenses/update-header-file-list' );
*
* var files = [
*     './foo/bar.js'
* ];
*
* var old = '// @license MIT';
*
* var header = '// This file is licensed under Apache-2.0.';
*
* updateHeader( files, old, header, done );
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* @example
* var updateHeader = require( '@stdlib/_tools/licenses/update-header-file-list' );
*
* var files = [
*     './foo/bar.js'
* ];
*
* var old = '// @license MIT';
*
* var header = '// This file is licensed under Apache-2.0.';
*
* var err = updateHeader.sync( files, old, header );
* if ( err instanceof Error ) {
*     throw err;
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;
