'use strict';

/**
* Update a license header in a source code string.
*
* @module @stdlib/_tools/licenses/update-header
*
* @example
* var updateHeader = require( '@stdlib/_tools/licenses/update-header' );
*
* var re = /\/\/ @license Apache-2\.0\n\n/;
*
* var h = '/* @license Apache-2.0 *\/';
*
* var src = [
*     '// @license Apache-2.0',
*     '',
*     '"use strict";',
*     '',
*     'var x = 3.14;',
*     ''
* ];
*
* var out = updateHeader( src.join( '\n' ), re, h );
* // returns <string>
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
