'use strict';

/**
* Validate an array of link objects against a link database.
*
* @module @stdlib/_tools/links/validate
*
* @example
* var validate = require( '@stdlib/_tools/links/validate' );
*
* var links = [
*     {
*         'id': 'gumbel-distribution',
*         'url': 'https://en.wikipedia.org/wiki/Gumbel_distribution'
*     },
*     {
*         'id': 'gumbel-dist',
*         'url': 'https://en.wikipedia.org/wiki/Gumbel_distribution'
*     },
*     {
*         'id': 'gumbel-distribution',
*         'url': 'https://mathworld.wolfram.com/GumbelDistribution.html'
*     }
* ];
* validate( links, clbk );
*
* function clbk( error, validationErrors ) {
*   if ( error ) {
*       throw error;
*   }
*   console.log( validationErrors );
*   // => [...]
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var validate = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( validate, 'sync', sync );


// EXPORTS //

module.exports = validate;
