'use strict';

// MODULES //

var tape = require( 'tape' );
var IS_BROWSER = require( '@stdlib/assert/is-browser' );
var lint = require( './../lib/lint.sync.js' );


// VARIABLES //

var opts = {
	'skip': IS_BROWSER
};


// TESTS //

tape( 'main export is a function', opts, function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof lint, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an error if an error is encountered when attempting to read a file', opts, function test( t ) {
	var files;
	var err;

	files = [
		'akfjadlfjsdlakfjsafjlsadjfkljfklasjfkladsjfklajadfkladsfljaie.js' // File does not exist!
	];
	err = lint( files, {} );

	t.strictEqual( err instanceof Error, true, 'returns an error' );
	t.end();
});
