'use strict';

/**
* Lint the license header for each file in a provided file list.
*
* @module @stdlib/_tools/lint/license-header-file-list
*
* @example
* var lint = require( '@stdlib/_tools/lint/license-header-file-list' );
*
* var files = [
*     __filename
* ];
*
* var header = '// This file is licensed under Apache-2.0.';
*
* lint( files, header, onLint );
*
* function onLint( error, errs ) {
*     if ( error ) {
*         throw error;
*     }
*     console.error( errs );
* }
*
* @example
* var lint = require( '@stdlib/_tools/lint/license-header-file-list' );
*
* var files = [
*     __filename
* ];
*
* var header = '// This file is licensed under Apache-2.0.';
*
* var errs = lint.sync( files, header );
* if ( errs instanceof Error ) {
*     throw errs;
* }
* console.error( errs );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;
