'use strict';

// MODULES //

var join = require( 'path' ).join;
var readDir = require( '@stdlib/fs/read-dir' ).sync;


// VARIABLES //

var DIRS = [
	[ 'bad', 'bad' ], // [ <dirname>, <key_value> ]
	[ 'good', 'good' ],
	[ 'multiple', 'multiple' ],
	[ 'start-location', 'startLocation' ]
];


// MAIN //

/**
* Returns an object containing fixtures file lists.
*
* @private
* @returns {Object} object containing fixtures file lists
*/
function main() {
	var list;
	var out;
	var dir;
	var key;
	var j;
	var i;

	out = {};
	for ( i = 0; i < DIRS.length; i++ ) {
		key = DIRS[ i ][ 1 ];
		dir = join( __dirname, DIRS[ i ][ 0 ] );
		list = readDir( dir );
		for ( j = 0; j < list.length; j++ ) {
			// Assemble absolute file paths:
			list[ j ] = join( dir, list[ j ] );
		}
		out[ key ] = list;
	}
	return out;
} // end FUNCTION main()


// EXPORTS //

module.exports = main;
