tape( 'if provided multiple header values and one or more of those values lacks a language prefix, the command-line interface prints an error and sets a non-zero exit code', opts, function test( t ) {
	var opts;
	var cmd;
	var len;
	var str;
	var i;

	str = ' ';
	len = fixtures.good.length;
	for ( i = 0; i < len; i++ ) {
		str += 'process.argv[ '+(2+i)+' ] = \''+fixtures.good[i]+'\';';
		if ( i < len-1 ) {
			str += ' ';
		}
	}
	cmd = [
		EXEC_PATH,
		'-e',
		'"process.stdin.isTTY = true;'+str+' process.argv[ '+(2+i)+' ] = \'--header=awk:./fixtures/headers/minimal_string.txt\'; process.argv[ '+(3+i)+' ] = \'--header=./fixtures/headers/minimal_string.txt\'; process.argv[ '+(4+i)+' ] = \'--header=default:./fixtures/headers/re_apache.txt\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'cwd': __dirname
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var msg;
		if ( error ) {
			t.pass( error.message );
			t.strictEqual( error.code, 1, 'expected exit code' );
		}
		t.strictEqual( stdout.toString(), '', 'does not print to `stdout`' );

		msg = stderr.toString();
		t.strictEqual( msg.length > 0, true, msg );
		t.end();
	}
});

