'use strict';

/**
* Lint license headers for a file glob.
*
* @module @stdlib/_tools/lint/license-header-glob
*
* @example
* var lint = require( '@stdlib/_tools/lint/license-header-glob' );
*
* var opts = {
*     'dir': __dirname
* };
*
* lint( opts, onLint );
*
* function onLint( error, errs ) {
*     if ( error ) {
*         throw error;
*     }
*     console.error( errs );
* }
*
* @example
* var lint = require( '@stdlib/_tools/lint/license-header-glob' );
*
* var opts = {
*     'dir': __dirname
* };
*
* var errs = lint.sync( opts );
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;
