'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var glob = require( 'glob' );
var logger = require( 'debug' );
var isFunction = require( '@stdlib/assert/is-function' );
var cwd = require( '@stdlib/process/cwd' );
var copy = require( '@stdlib/utils/copy' );
var format = require( '@stdlib/string/format' );
var linter = require( '@stdlib/_tools/lint/license-header-file-list' );
var DEFAULTS = require( './defaults.js' );
var validate = require( './validate.js' );


// VARIABLES //

var debug = logger( 'lint:license-header-glob:async' );


// MAIN //

/**
* Asynchronously lints license headers for a file glob.
*
* @param {Options} [options] - function options
* @param {string} [options.dir] - root directory from which to search for files
* @param {string} [options.pattern='**\/*'] - filename pattern
* @param {StringArray} [options.ignore] - glob pattern(s) to exclude matches
* @param {(string|RegExp|Object)} [options.header] - license header to lint against
* @param {Callback} clbk - callback to invoke after linting
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {TypeError} callback argument must be a function
*
* @example
* var opts = {
*     'dir': __dirname
* };
*
* lint( opts, onLint );
*
* function onLint( error, errs ) {
*     if ( error ) {
*         throw error;
*     }
*     console.error( errs );
* }
*/
function lint() {
	var options;
	var gopts;
	var opts;
	var clbk;
	var err;
	var dir;

	opts = copy( DEFAULTS );
	if ( arguments.length < 2 ) {
		clbk = arguments[ 0 ];
	} else {
		options = arguments[ 0 ];
		clbk = arguments[ 1 ];
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	if ( !isFunction( clbk ) ) {
		throw new TypeError( format( 'invalid argument. Callback argument must be a function. Value: `%s`.', clbk ) );
	}
	debug( 'Options: %s', JSON.stringify( opts ) );
	if ( opts.dir ) {
		dir = resolve( cwd(), opts.dir );
	} else {
		dir = cwd();
	}
	debug( 'Directory: %s', dir );

	gopts = {
		'cwd': dir,
		'ignore': opts.ignore,
		'realpath': true, // return absolute file paths
		'nodir': true // do not match directories
	};
	debug( 'Glob options: %s', JSON.stringify( gopts ) );

	debug( 'Searching for files...' );
	glob( opts.pattern, gopts, onGlob );

	/**
	* Callback invoked after matching files.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(EmptyArray|StringArray)} names - list of matching files
	* @returns {void}
	*/
	function onGlob( error, names ) {
		if ( error ) {
			debug( 'Encountered an error when searching for files. Error: %s', error.message );
			return done( error );
		}
		debug( 'Found %d matching files.', names.length );
		if ( names.length === 0 ) {
			debug( 'No files to lint.' );
			return done( null, [] );
		}
		debug( 'Linting files...' );
		linter( names, opts.header, onLint );
	}

	/**
	* Callback invoked after linting files.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(EmptyArray|ObjectArray)} errs - lint errors
	* @returns {void}
	*/
	function onLint( error, errs ) {
		if ( error ) {
			debug( 'Encountered an error when linting files. Error: %s', error.message );
			return done( error );
		}
		debug( 'Found %d lint errors.', errs.length );
		done( null, errs );
	}

	/**
	* Callback invoked upon completion.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(EmptyArray|ObjectArray)} errs - lint errors
	* @returns {void}
	*/
	function done( error, errs ) {
		if ( error ) {
			return clbk( error );
		}
		debug( 'Finished linting.' );
		clbk( null, errs );
	}
}


// EXPORTS //

module.exports = lint;
