tape( 'the command-line interface prints nothing if a file contains a valid license header (string)', opts, function test( t ) {
	var opts;
	var cmd;

	cmd = [
		proc.execPath,
		'-e',
		'"process.stdin.isTTY = true; process.argv[ 2 ] = \'./fixtures/file.js.txt\'; process.argv[ 3 ] = \'--header=./fixtures/apache.single_line.txt\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'cwd': __dirname
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( stdout.toString(), '', 'expected value' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

