tape( 'the command-line interface prints an error message if a file does not contain a valid license header (string)', opts, function test( t ) {
	var opts;
	var cmd;

	cmd = [
		proc.execPath,
		'-e',
		'"process.stdin.isTTY = true; process.argv[ 2 ] = \'./fixtures/file.js.txt\'; process.argv[ 3 ] = \'--header=./fixtures/mit.single_line.txt\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'cwd': __dirname
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var results;
		if ( error ) {
			t.pass( error.message );
			t.strictEqual( stdout.toString(), '', 'does not print to `stdout`' );

			results = stderr.toString().split( RE_EOL );
			t.strictEqual( results.length-1, 1, 'returns expected number of errors' );
			t.strictEqual( results[0], 'Error: malformed or missing license header.', 'prints expected message' );
		} else {
			t.fail( 'process should error' );
		}
		t.end();
	}
});

