tape( 'the command-line interface prints an error message if unable to read a `header` filepath', opts, function test( t ) {
	var opts;
	var cmd;

	cmd = [
		proc.execPath,
		fpath,
		'--header=./fixtures/dkjfaljdflasjlfs/dadafksj/txtlljt.txt', // non-existent file path
		'./fixtures/file.js.txt'
	];
	opts = {
		'cwd': __dirname
	};

	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var results;
		if ( error ) {
			t.pass( error.message );
			t.strictEqual( error.code, 1, 'expected exit code' );
		} else {
			t.fail( 'process should error' );
		}
		t.strictEqual( stdout.toString(), '', 'does not print to `stdout`' );

		results = stderr.toString().split( RE_EOL );
		t.strictEqual( results.length-1, 1, 'returns expected number of errors' );
		t.strictEqual( results[0].length > 0, true, 'prints an error message' );

		t.end();
	}
});

