'use strict';

/**
* Generate a table of contents for a namespace.
*
* @module @stdlib/_tools/markdown/namespace-toc
*
* @example
* var resolve = require( 'path' ).resolve;
* var createTOC = require( '@stdlib/_tools/markdown/namespace-toc' );
*
* var opts = {
*     'dir': resolve( __dirname, '..' )
* };
* createTOC( opts, onCreate );
*
* function onCreate( error, res ) {
*     if ( error ) {
*         throw error;
*     }
*     // String with table of contents:
*     console.log( res.toc );
*
*     // Array of package links:
*     console.log( res.links );
* }
*
* @example
* var resolve = require( 'path' ).resolve;
* var createTOC = require( '@stdlib/_tools/markdown/namespace-toc' );
*
* var opts = {
*     'dir': resolve( __dirname, '..' )
* };
* var out = createTOC.sync( opts );
* // returns {...}
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var createTOC = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( createTOC, 'sync', sync );


// EXPORTS //

module.exports = createTOC;
