'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var logger = require( 'debug' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var isFunction = require( '@stdlib/assert/is-function' );
var copy = require( '@stdlib/utils/copy' );
var cwd = require( '@stdlib/process/cwd' );
var dirname = require( '@stdlib/utils/dirname' );
var format = require( '@stdlib/string/format' );
var defaults = require( './defaults.json' );
var validate = require( './validate.js' );
var walkFile = require( './walk_file.js' );
var resolveAliases = require( './resolve_aliases.js' );
var toArray = require( './to_array.js' );


// VARIABLES //

var debug = logger( 'module-pkg-deps:async' );


// MAIN //

/**
* Lists package dependencies.
*
* @param {(string|StringArray)} files - file or file list
* @param {Options} [options] - function options
* @param {boolean} [options.builtins=true] - boolean indicating whether to include built-in packages
* @param {boolean} [options.walk=true] - boolean indicating whether to walk relative module dependencies
* @param {Object} [options.aliases={}] - object mapping relative module dependency identifiers to other module identifiers
* @param {Callback} clbk - callback
* @throws {TypeError} first argument must be an array of strings
* @throws {TypeError} callback argument must be a function
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
*
* @example
* var files = [ '/foo/bar/index.js' ];
*
* pkgDeps( files, clbk );
*
* function clbk( error, deps ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( deps );
* }
*/
function pkgDeps( files, options, clbk ) {
	var aliases;
	var errFLG;
	var isStr;
	var count;
	var cache;
	var list;
	var file;
	var opts;
	var err;
	var len;
	var dir;
	var cb;
	var i;
	var k;

	isStr = isString( files );
	if (
		!isStr &&
		!isStringArray( files )
	) {
		throw new TypeError( format( 'invalid argument. First argument must be either a string or array of strings. Value: `%s`.', files ) );
	}
	if ( isStr ) {
		list = [ files ];
	} else {
		list = files;
	}
	opts = copy( defaults );
	if ( arguments.length < 3 ) {
		cb = options;
	} else {
		cb = clbk;
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	if ( !isFunction( cb ) ) {
		throw new TypeError( format( 'invalid argument. Callback argument must be a function. Value: `%s`.', cb ) );
	}
	debug( 'Options: %s', JSON.stringify( opts ) );
	aliases = opts.aliases;

	dir = cwd();
	debug( 'Current working directory: %s', dir );

	len = list.length;
	cache = {};
	count = 0;

	debug( 'Walking %s files...', len );
	for ( i = 0; i < len; i++ ) {
		k = i + 1;
		file = resolve( dir, list[ i ] );

		debug( 'Resolving aliases for file: %s (%d of %d)...', file, k, len );
		opts.aliases = resolveAliases( dirname( file ), aliases );
		debug( 'Resolved aliases for file: $s (%d of %d). Aliases: %s.', file, k, len, JSON.stringify( opts.aliases ) );

		debug( 'Walking file: %s (%d of %d)...', file, k, len );
		walkFile( cache, file, opts, createCallback( i ) );
	}

	/**
	* Returns a callback to be invoked upon walking a file.
	*
	* @private
	* @param {NonNegativeInteger} idx - index
	* @returns {Callback} callback
	*/
	function createCallback( idx ) {
		var file = list[ idx ];
		var k = idx + 1;
		return onWalk;
		/**
		* Callback invoked upon walking a file.
		*
		* @private
		* @param {(Error|null)} error - error object
		* @returns {void}
		*/
		function onWalk( error ) {
			if ( errFLG ) {
				return;
			}
			if ( error ) {
				debug( 'Encountered an error when walking file: %s (%d of %d). Error: %s', file, k, len, error.message );
				errFLG = true;
				return cb( error );
			}
			debug( 'Successfully walked file: %s (%d of %d).', file, k, len );

			count += 1;
			debug( 'Walked %d of %d files.', count, len );
			if ( count === len ) {
				return cb( null, toArray( cache ) );
			}
		}
	}
}


// EXPORTS //

module.exports = pkgDeps;
