'use strict';

// MODULES //

var join = require( 'path' ).join;
var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var instanceOf = require( '@stdlib/assert/instance-of' );
var pkgDeps = require( './../lib/async.js' );


// FUNCTIONS //

/**
* Sort function for ensuring deterministic comparison order.
*
* @private
* @param {Object} a - first value
* @param {Object} b - second value
* @returns {number} value indicating whether to sort `a` before `b`
*/
function sort( a, b ) {
	if ( a.file < b.file ) {
		return 1;
	}
	if ( a.file > b.file ) {
		return -1;
	}
	return 0;
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof pkgDeps, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided a first argument which is not a string or string array, the function throws an error (no options)', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		null,
		undefined,
		[],
		[ '5', null ],
		[ 'beep', 5 ],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			pkgDeps( value, noop );
		};
	}
});

