'use strict';

// MAIN //

/**
* Returns default options.
*
* @private
* @returns {Object} default options
*/
function defaults() {
	return {
		'protocol': 'https',
		'hostname': 'www.npmjs.com',
		'port': 443,
		'pathname': '/browse/depended/',
		'query': '',
		'method': 'GET',
		'useragent': 'https://github.com/stdlib-js/stdlib/@stdlib/_tools/npm/pkg-dependents'
	};
}


// EXPORTS //

module.exports = defaults;
