'use strict';

// MODULES //

var resolve = require( './resolve.sync.js' );
var metafilter = require( './filter_meta.js' );


// MAIN //

/**
* Synchronously filters a list of package names for packages which are compatible with browser environments.
*
* @private
* @param {StringArray} names - package names
* @param {string} dir - base directory from which to resolve packages
* @returns {(StringArray|EmptyArray)} filtered list of package names
*/
function filter( names, dir ) {
	names = resolve( names, dir );
	return metafilter( names );
}


// EXPORTS //

module.exports = filter;
