'use strict';

// MODULES //

var dirname = require( '@stdlib/utils/dirname' );


// MAIN //

/**
* Transforms a raw file list.
*
* @private
* @param {(EmptyArray|StringArray)} files - list of absolute file paths
* @returns {(EmptyArray|StringArray)} list of packages
*
* @example
* var files = [ '/stdlib/stdlib/lib/node_modules/@stdlib/constants/float64/min-safe-integer/package.json' ];
*
* var pkgs = transform( files );
* // returns [ '/stdlib/stdlib/lib/node_modules/@stdlib/constants/float64/min-safe-integer' ]
*/
function transform( files ) {
	var out;
	var i;
	out = [];
	for ( i = 0; i < files.length; i++ ) {
		out.push( dirname( files[ i ] ) );
	}
	return out;
}


// EXPORTS //

module.exports = transform;
