'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var noop = require( '@stdlib/utils/noop' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var isArray = require( '@stdlib/assert/is-array' );
var findIncludes = require( './../lib/main.js' );


// VARIABLES //

var dir = resolve( __dirname, '..' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof findIncludes, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	t.throws( foo, TypeError, 'throws error' );
	t.end();
	function foo() {
		var opts = {
			'dir': null
		};
		findIncludes( opts, noop );
	}
});

tape( 'if provided a callback argument which is not a function, the function throws an error (no options)', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		void 0,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			findIncludes( value );
		};
	}
});

tape( 'if provided a callback argument which is not a function, the function throws an error (options)', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		void 0,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			findIncludes( {}, value );
		};
	}
});

tape( 'the function returns an error to a provided callback if an error is encountered while searching a directory', function test( t ) {
	var findIncludes = proxyquire( './../lib/main.js', {
		'glob': glob
	});

	findIncludes( clbk );

	function glob() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( new Error( 'beep' ) );
		}
	}

	function clbk( error ) {
		t.ok( error, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an error to a provided callback if an error is encountered while resolving packages containing an `include` directory', function test( t ) {
	var findIncludes;
	var opts;

	findIncludes = proxyquire( './../lib/main.js', {
		'./inspect.js': inspect
	});
	opts = {
		'dir': dir
	};

	findIncludes( opts, clbk );

	function inspect() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( new Error( 'beep' ) );
		}
	}

	function clbk( error ) {
		t.ok( error, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an empty array if unable to resolve any packages containing an `include` directory', function test( t ) {
	var findIncludes = proxyquire( './../lib/main.js', {
		'glob': glob
	});

	findIncludes( clbk );

	function glob() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( null, [] );
		}
	}

	function clbk( error, dirs ) {
		if ( error ) {
			t.ok( false, error.message );
		}
		t.strictEqual( isArray( dirs ), true, 'returns an array' );
		t.strictEqual( dirs.length, 0, 'returns an empty array' );
		t.end();
	}
});

tape( 'the function returns a string array (if at least one package containing an `include` directory is detected)', function test( t ) {
	var findIncludes;
	var opts;

	findIncludes = proxyquire( './../lib/main.js', {
		'./inspect.js': inspect
	});

	opts = {
		'dir': dir
	};
	findIncludes( opts, clbk );

	function inspect() {
		var clbk = arguments[ arguments.length-1 ];
		clbk( null, [ dir ] );
	}

	function clbk( error, dirs ) {
		if ( error ) {
			t.ok( false, error.message );
		}
		t.strictEqual( isStringArray( dirs ), true, 'returns a string array' );
		t.end();
	}
});
