'use strict';

/**
* Deterministically convert a package name (or a list of package names) to a bucket index.
*
* @module @stdlib/_tools/pkgs/name2bucket
*
* @example
* var name2bucket = require( '@stdlib/_tools/pkgs/name2bucket' );
*
* var out = name2bucket( '@stdlib/math/base/special/sin', 10 );
* // returns <number>
*
* @example
* var name2bucket = require( '@stdlib/_tools/pkgs/name2bucket' );
*
* var pkgs = [
*     '@stdlib/math/base/special/sin'
* ];
*
* var out = name2bucket( pkgs, 10 );
* // returns [ <number> ]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
