'use strict';

// MODULES //

var tape = require( 'tape' );
var name2bucket = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof name2bucket, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a string or an array of strings for the first argument', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[ 'foo', 5 ],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			name2bucket( value, 10 );
		};
	}
});

tape( 'the function throws an error if not provided a positive integer for the second argument', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		-5,
		3.14,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			name2bucket( 'foo', value );
		};
	}
});

tape( 'the function returns `0` if provided an empty string', function test( t ) {
	t.strictEqual( name2bucket( '', 10 ), 0, 'returns expected value' );
	t.end();
});

tape( 'the function converts a package name to a bucket', function test( t ) {
	t.strictEqual( name2bucket( '@stdlib/math/base/special/sin', 10 ), 1, 'returns expected value' );
	t.end();
});

tape( 'the function converts standalone and non-standalone package names to the same bucket', function test( t ) {
	t.strictEqual( name2bucket( '@stdlib/math/base/special/sin', 10 ), name2bucket( '@stdlib/math-base-special-sin', 10 ), 'returns expected value' );
	t.end();
});

tape( 'the function supports providing an array of package names', function test( t ) {
	var expected;
	var actual;
	var names;

	names = [
		'@stdlib/math/base/special/sin',
		'@stdlib/math/base/special/cos'
	];
	expected = [
		1,
		6
	];
	actual = name2bucket( names, 10 );
	t.deepEqual( actual, expected, 'returns expected value' );
	t.end();
});

tape( 'the function returns an empty array if provided an empty array', function test( t ) {
	var arr;
	var out;

	arr = [];
	out = name2bucket( arr, 10 );

	t.notEqual( arr, out, 'returns different reference' );
	t.deepEqual( out, [], 'returns expected value' );
	t.end();
});
