'use strict';

/**
* Convert a stdlib package name (or a list of package names) to a standalone package name.
*
* @module @stdlib/_tools/pkgs/name2standalone
*
* @example
* var name2standalone = require( '@stdlib/_tools/pkgs/name2standalone' );
*
* var name = name2standalone( '@stdlib/math/base/special/sin' );
* // returns '@stdlib/math-base-special-sin'
*
* @example
* var name2standalone = require( '@stdlib/_tools/pkgs/name2standalone' );
*
* var pkgs = [
*     '@stdlib/math/base/special/sin'
* ];
*
* var out = name2standalone( pkgs );
* // returns [ '@stdlib/math-base-special-sin' ]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
