'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var logger = require( 'debug' );
var isNodeBuiltin = require( '@stdlib/assert/is-node-builtin' );
var readFileSync = require( '@stdlib/fs/read-file' ).sync;
var startsWith = require( '@stdlib/string/starts-with' );
var contains = require( '@stdlib/assert/contains' );
var instanceOf = require( '@stdlib/assert/instance-of' );
var pkgDeps = require( '@stdlib/_tools/pkgs/deps' ).sync;
var readJSON = require( '@stdlib/fs/read-json' ).sync;
var dirname = require( '@stdlib/utils/dirname' );
var prunePackage = require( './prune_package.js' );
var unique = require( './unique.js' );
var getRoot = require( './root.js' );


// VARIABLES //

var debug = logger( 'pkgs:namespace-deps' );
var STDLIB_TYPES = '/// <reference types="@stdlib/types"/>';


// MAIN //

/**
* Returns a list of namespace dependencies for a specified package.
*
* @private
* @param {string} pkgName - package name
* @param {PositiveInteger} level - dependency level
* @param {boolean} dev - boolean indicating whether to return development dependencies
* @returns {(StringArray|EmptyArray)} dependencies
*/
function namespaceDeps( pkgName, level, dev ) {
	var namespacePkgs;
	var fileDeps;
	var manifest;
	var fpath;
	var entry;
	var conf;
	var deps;
	var name;
	var task;
	var def;
	var pkg;
	var i;
	var j;

	namespacePkgs = pkgDeps( [ pkgName ], {
		'dev': dev,
		'dir': getRoot( '' )
	});
	if ( dev ) {
		namespacePkgs = namespacePkgs[ 0 ].devDeps;
	} else {
		namespacePkgs = namespacePkgs[ 0 ].deps;
	}
	deps = [];
	try {
		entry = dirname( require.resolve( pkgName ) );
		fpath = resolve( entry, '..', 'docs', 'types', 'index.d.ts' );
		def = readFileSync( fpath, 'utf-8' );
		if ( !dev && contains( def, STDLIB_TYPES ) ) {
			deps.push( '@stdlib/types' );
		}
	} catch ( err ) {
		debug( 'Encountered an error while reading `index.d.ts` file: %s', err.message );
	}
	fpath = resolve( entry, '..', 'manifest.json' );
	manifest = readJSON( fpath );
	if ( !instanceOf( manifest, Error ) ) {
		if ( !dev ) {
			name = prunePackage( '@stdlib/utils/library-manifest', level );
			deps.push( name );
		}
		task = manifest.options.task;
		for ( i = 0; i < manifest.confs.length; i++ ) {
			conf = manifest.confs[ i ];
			if ( !dev && conf.task === task ) {
				for ( j = 0; j < conf.dependencies.length; j++ ) {
					if ( !startsWith( conf.dependencies[ j ], pkgName ) ) {
						name = prunePackage( conf.dependencies[ j ], level );
						deps.push( name );
					}
				}
			} else if ( dev ) {
				for ( j = 0; j < conf.dependencies.length; j++ ) {
					if ( !startsWith( conf.dependencies[ j ], pkgName ) ) {
						name = prunePackage( conf.dependencies[ j ], level );
						deps.push( name );
					}
				}
			}
		}
	}
	if ( !namespacePkgs || namespacePkgs.length === 0 ) {
		return unique( deps );
	}
	for ( i = 0; i < namespacePkgs.length; i++ ) {
		pkg = namespacePkgs[ i ];
		if ( pkg === pkgName ) {
			continue;
		}
		if (
			startsWith( pkg, pkgName ) ||
			(
				pkgName === '@stdlib/plot/ctor' &&
				startsWith( pkg, '@stdlib/plot' )
			)
		) {
			fileDeps = namespaceDeps( pkg, level, dev );
			for ( j = 0; j < fileDeps.length; j++ ) {
				if (
					!startsWith( fileDeps[ j ], pkgName ) &&
					fileDeps[ j ] !== pkg
				) {
					deps.push( fileDeps[ j ] );
				}
			}
		} else {
			name = prunePackage( pkg, level );
			if ( !isNodeBuiltin( name ) ) {
				deps.push( name );
			}
		}
	}
	return unique( deps );
}


// EXPORTS //

module.exports = namespaceDeps;
