'use strict';

// MODULES //

var tape = require( 'tape' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var namespaceDeps = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof namespaceDeps, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided a first argument which is not a string, the function throws an error', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			namespaceDeps( value );
		};
	}
});

tape( 'if provided a `options` argument which is not an object, the function throws an error', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			namespaceDeps( '@stdlib/regexp', value );
		};
	}
});

tape( 'the function throws an error if provided an invalid option', function test( t ) {
	t.throws( foo, TypeError, 'throws error' );
	t.end();
	function foo() {
		var opts = {
			'dev': 123
		};
		namespaceDeps( '@stdlib/regexp', opts );
	}
});

tape( 'the function returns a string array', function test( t ) {
	var deps = namespaceDeps( '@stdlib/regexp' );
	t.equal( isStringArray( deps ), true, 'returns a string array' );
	t.end();
});

tape( 'the function returns a string array (dev option)', function test( t ) {
	var deps;
	var opts;

	opts = {
		'dev': true
	};
	deps = namespaceDeps( '@stdlib/regexp', opts );

	t.equal( isStringArray( deps ), true, 'returns a string array' );
	t.end();
});

tape( 'the function returns a string array (level option)', function test( t ) {
	var deps;
	var opts;

	opts = {
		'level': 20
	};
	deps = namespaceDeps( '@stdlib/regexp', opts );

	t.equal( isStringArray( deps ), true, 'returns a string array' );
	t.end();
});
