'use strict';

/**
* Find namespace package READMEs.
*
* @module @stdlib/_tools/pkgs/namespace-readmes
*
* @example
* var findREADMEs = require( '@stdlib/_tools/pkgs/namespace-readmes' );
*
* findREADMEs( clbk );
*
* function clbk( error, files ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( files );
* }
*
* @example
* var findREADMEs = require( '@stdlib/_tools/pkgs/namespace-readmes' );
*
* var files = findREADMEs.sync();
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;
