'use strict';

// MODULES //

var glob = require( 'glob' );
var isFunction = require( '@stdlib/assert/is-function' );
var format = require( '@stdlib/string/format' );
var copy = require( '@stdlib/utils/copy' );
var config = require( './config.json' );
var validate = require( './validate.js' );
var getRoot = require( './root.js' );
var transform = require( './transform.js' );
var filter = require( './filter.js' );
var find = require( './find.js' ); // eslint-disable-line stdlib/no-redeclare


// MAIN //

/**
* Asynchronously finds namespace package READMEs.
*
* @param {Options} [options] - function options
* @param {string} [options.dir] - directory from which to search for namespace package READMEs
* @param {string} [options.pattern='**\/package.json'] - glob pattern
* @param {StringArray} [options.ignore] - glob pattern(s) to exclude matches
* @param {Callback} clbk - callback to invoke after finding namespace package READMEs
* @throws {TypeError} callback argument must be a function
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {Error} `pattern` option must end with `package.json`
*
* @example
* findREADMEs( clbk );
*
* function clbk( error, files ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( files );
* }
*/
function findREADMEs() {
	var options;
	var gopts;
	var opts;
	var clbk;
	var err;

	opts = copy( config );
	if ( arguments.length < 2 ) {
		clbk = arguments[ 0 ];
	} else {
		options = arguments[ 0 ];
		clbk = arguments[ 1 ];
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	if ( !isFunction( clbk ) ) {
		throw new TypeError( format( 'invalid argument. Callback argument must be a function. Value: `%s`.', clbk ) );
	}
	// Find `package.json` files...
	gopts = {
		'cwd': getRoot( opts.dir || '' ),
		'ignore': opts.ignore.concat( config.ignore ), // always include default ignore patterns
		'realpath': true // return absolute file paths
	};
	glob( opts.pattern, gopts, onGlob );

	/**
	* Callback invoked after matching files.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(EmptyArray|StringArray)} files - list of matching files
	* @returns {void}
	*/
	function onGlob( error, files ) {
		var pkgs;
		var tmp;
		var pkg;
		var i;
		var j;
		if ( error ) {
			return done( error );
		}
		if ( files.length ) {
			// Resolve package data:
			pkgs = transform( files );

			// Resolve a list of namespaces:
			tmp = filter( pkgs );

			// Extract namespace package data...
			for ( i = 0; i < tmp.length; i++ ) {
				pkg = tmp[ i ];
				for ( j = 0; j < pkgs.length; j++ ) {
					if ( pkgs[ j ].pkg === pkg ) {
						tmp[ i ] = pkgs[ j ];
						break;
					}
				}
			}
			pkgs = tmp;

			// Search for package READMEs:
			if ( pkgs.length ) {
				return find( pkgs, done );
			}
		}
		return done( null, [] );
	}

	/**
	* Callback invoked upon finding namespace READMEs.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {(EmptyArray|StringArray)} files - files
	* @returns {void}
	*/
	function done( error, files ) {
		if ( error ) {
			return clbk( error );
		}
		clbk( null, files );
	}
}


// EXPORTS //

module.exports = findREADMEs;
