'use strict';

/**
* List stdlib standalone package names.
*
* @module @stdlib/_tools/pkgs/standalones
*
* @example
* var ls = require( '@stdlib/_tools/pkgs/standalones' );
*
* ls( onList );
*
* function onList( error, names ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( names );
* }
*
* @example
* var ls = require( '@stdlib/_tools/pkgs/standalones' );
*
* var names = ls.sync();
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var ls = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( ls, 'sync', sync );


// EXPORTS //

module.exports = ls;
