'use strict';

/**
* stdlib package tree.
*
* @module @stdlib/_tools/pkgs/tree
*
* @example
* var pkgTree = require( '@stdlib/_tools/pkgs/tree' );
*
* pkgTree( onTree );
*
* function onTree( error, tree ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( tree );
* }
*
* @example
* var pkgTree = require( '@stdlib/_tools/pkgs/tree' );
*
* var tree = pkgTree.sync();
* // returns {...}
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var ls = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( ls, 'sync', sync );


// EXPORTS //

module.exports = ls;
