'use strict';

/**
* Find packages containing WebAssembly.
*
* @module @stdlib/_tools/pkgs/wasm
*
* @example
* var findWASM = require( '@stdlib/_tools/pkgs/wasm' );
*
* findWASM( clbk );
*
* function clbk( error, pkgs ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( pkgs );
* }
*
* @example
* var findWASM = require( '@stdlib/_tools/pkgs/wasm' );
*
* var pkgs = findWASM.sync();
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var findWASM = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( findWASM, 'sync', sync );


// EXPORTS //

module.exports = findWASM;
