'use strict';

/**
* remark plugin to run JavaScript examples.
*
* @module @stdlib/_tools/remark/plugins/remark-run-javascript-examples
*
* @example
* var remark = require( 'remark' );
* var run = require( '@stdlib/_tools/remark/plugins/remark-run-javascript-examples' );
*
* var str = [
*     '<section class="usage">',
*     '',
*     '## Usage',
*     '',
*     '```javascript',
*     'var path = require( "path" );',
*     '```',
*     '',
*     '#### path.posix.join( [...paths] )',
*     '',
*     '```javascript',
*     'var p = path.posix.join( "foo", "bar" );',
*     '```',
*     '',
*     '</section>',
*     '',
*     '<!-- /.usage -->',
*     '',
*     '<section class="examples">',
*     '',
*     '## Examples',
*     '',
*     '```javascript',
*     'console.log( "HELLO WORLD!" );',
*     '```',
*     '</section>',
*     '',
*     '<!-- /.examples -->',
*     ''
* ];
*
* remark().use( run ).process( str.join( '\n' ), done );
* // => 'HELLO WORLD'
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
