'use strict';

// MODULES //

var replace = require( '@stdlib/string/base/replace' );


// VARIABLES //

var RE_MARKDOWN_OR_CODE = /(```[\s\S]+?```|`[\s\S]*?`)|([\\*_[#!~|])/g;


// MAIN //

/**
* Escapes Markdown special characters in a string.
*
* ## Notes
*
* -   This function escapes characters that have special meanings in Markdown, such as #, *, _, ~, etc., by prefixing them with a backslash.
* -   The function does not escape characters inside code.
*
* @private
* @param {string} str - string to escape
* @returns {string} escaped string
*
* @example
* var out = escapeMarkdown( 'Returns [index, value] pairs. ```No escape here: *[]!```' );
* // returns 'Returns \[index, value] pairs. ```No escape here: *[]!```'
*
* @example
* var out = escapeMarkdown( 'Outside code: *, inside code: `*`' );
* // returns 'Outside code: \*, inside code: `*`'
*/
function escapeMarkdown( str ) {
	return replace( str, RE_MARKDOWN_OR_CODE, onMatch );

	/**
	* Callback invoked for each match.
	*
	* @private
	* @param {string} match - matched substring
	* @param {string} code - code block or inline code
	* @param {string} markdown - Markdown characters
	* @returns {string} replacement string
	*/
	function onMatch( match, code, markdown ) {
		if ( code ) {
			return code;
		}
		return '\\' + markdown;
	}
}


// EXPORTS //

module.exports = escapeMarkdown;
