'use strict';

// MODULES //

var trim = require( '@stdlib/string/trim' );
var extractType = require( './extract_type.js' );


// VARIABLES //

var RE_TAG = /\n {4}([^ ]+?): (\.\.\.)?([^\n]+?) ?(\(optional\))?\n {8}([\s\S]+?)(?=\n\n {4}[^ ]|$)/g;


// MAIN //

/**
* Extracts all tags from the supplied section.
*
* @private
* @param {string} section - section contents
* @returns {ObjectArray} array of extracted tags
*/
function extractTags( section ) {
	var match;
	var tags;
	var type;
	var tag;

	tags = [];
	match = RE_TAG.exec( section );
	while ( match !== null ) {
		type = extractType( match[ 3 ] );
		tag = {
			'name': match[ 1 ],
			'type': type,
			'description': trim( match[ 5 ] ),
			'optional': match[ 4 ] === '(optional)'
		};
		if ( match[ 2 ] === '...' ) {
			tag.multiple = true;
		}
		tags.push( tag );
		match = RE_TAG.exec( section );
	}
	return tags;
}


// EXPORTS //

module.exports = extractTags;
