'use strict';

/**
* Parse the contents of a `repl.txt` file and return an abstract syntax tree (AST) representation.
*
* @module @stdlib/_tools/repl-txt/parse
*
* @example
* var parse = require( '@stdlib/_tools/repl-txt/parse' );
*
* var repl = [
*     '{{alias}}( value )',
*     '    Tests if a value is a number.',
*     '',
*     '    Parameters',
*     '    ----------',
*     '    value: any',
*     '        Value to test.',
*     '',
*     '    Returns',
*     '    -------',
*     '    bool: boolean',
*     '        Boolean indicating whether value is a number.',
*     '',
*     '    Examples',
*     '    --------',
*     '    > var bool = {{alias}}( 3.14 )',
*     '    true',
*     '    > bool = {{alias}}( new Number( 3.14 ) )',
*     '    true',
*     '    > bool = {{alias}}( NaN )',
*     '    true',
*     '    > bool = {{alias}}( null )',
*     '    false',
*     '',
*     '    See Also',
*     '    --------'
* ].join( '\n' );
*
* var ast = parse( repl );
* // returns [...]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
