'use strict';

// MODULES //

var Linter = require( './linter.js' );


// VARIABLES //

var linter = new Linter();


// MAIN //

/**
* Checks a `repl.txt` string for lint errors and attaches them to the function as an `errors` property.
*
* @param {string} str - `repl.txt` contents
* @param {Object} meta - meta-information
* @returns {boolean} boolean indicating whether the `repl.txt` contents pass linting
*/
function isValid( str, meta ) {
	linter.initialize( meta );
	linter.validate( str );

	isValid.errors = linter.errors;
	isValid.warnings = linter.warnings;
	if ( isValid.errors ) {
		return false;
	}
	return true;
}


// EXPORTS //

module.exports = isValid;
