'use strict';

var wrap = require( './../lib' );

var txt;
var out;

txt = 'Beep boop foo bar.';
out = wrap( txt );
console.log( out+'\n' );

txt = 'Rounds each element in a single-precision floating-point strided array `x` toward zero and assigns the results to elements in a single-precision floating-point strided array `y`.';
out = wrap( txt );
console.log( out+'\n' );

txt = 'Rounds each element in a single-precision floating-point strided array `x` toward positive infinity and assigns the results to elements in a single-precision floating-point strided array `y`.';
out = wrap( txt );
console.log( out+'\n' );

txt = 'Evaluates the ramp function for each element in a double-precision floating-point strided array `x` and assigns the results to elements in a double-precision floating-point strided array `y`.';
out = wrap( txt );
console.log( out+'\n' );
