'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var shell = require( 'child_process' ).execSync; // eslint-disable-line node/no-sync
var existsSync = require( '@stdlib/fs/exists' ).sync;
var readDir = require( '@stdlib/fs/read-dir' ).sync;
var readJSON = require( '@stdlib/fs/read-json' ).sync;
var objectKeys = require( '@stdlib/utils/keys' );
var extname = require( '@stdlib/utils/extname' );
var replace = require( '@stdlib/string/replace' );
var uppercase = require( '@stdlib/string/base/uppercase' );
var rootDir = require( '@stdlib/_tools/utils/root-dir' );
var log = require( '@stdlib/console/log' );


// VARIABLES //

var CREATE_ONLY = 1;
var SCAFFOLD_SCRIPT = resolve( __dirname, 'scaffold.sh' );
var ROOT_DIR = resolve( rootDir(), 'lib', 'node_modules' );
var DATA_DIR = resolve( __dirname, 'data' );
var RE_JSON = /\.json$/;
var OPTS = {
	'encoding': 'utf8'
};


// FUNCTIONS //

/**
* Filters a file list.
*
* @private
* @param {StringArray} files - file list
* @returns {StringArray} list of filtered files
*/
function filter( files ) {
	var out;
	var i;

	out = [];
	for ( i = 0; i < files.length; i++ ) {
		if ( RE_JSON.test( files[ i ] ) ) {
			out.push( files[ i ] );
		}
	}
	return out;
}

/**
* Reads a list of JSON files.
*
* @private
* @param {StringArray} files - file list
* @returns {Object} file contents
*/
function readJSONFiles( files ) {
	var fpath;
	var out;
	var ext;
	var f;
	var i;

	out = {};
	for ( i = 0; i < files.length; i++ ) {
		f = files[ i ];
		fpath = resolve( DATA_DIR, f );
		ext = extname( f );
		out[ replace( f, ext, '' ) ] = readJSON( fpath, OPTS );
	}
	return out;
}


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var names;
	var files;
	var DATA;
	var keys;
	var envs;
	var str;
	var cmd;
	var p;
	var k;
	var v;
	var o;
	var i;
	var j;

	files = readDir( DATA_DIR );
	files = filter( files );
	DATA = readJSONFiles( files );

	keys = objectKeys( DATA );
	for ( i = 0; i < keys.length; i++ ) {
		o = DATA[ keys[ i ] ];
		p = resolve( ROOT_DIR, '@'+o.pkg, 'package.json' );
		if ( existsSync( p ) ) {
			if ( CREATE_ONLY ) {
				log( 'Package already exists. Skipping @%s...', o.pkg );
				continue;
			}
			log( 'Updating package: @%s...', o.pkg );
		} else {
			log( 'Creating package: @%s...', o.pkg );
		}
		names = objectKeys( o );
		envs = [];
		for ( j = 0; j < names.length; j++ ) {
			k = names[ j ];
			v = o[ k ];
			str = uppercase( k );
			str += '=';
			str += '\'' + v + '\'';
			envs.push( str );
		}
		cmd = envs.join( ' ' ) + ' . ' + SCAFFOLD_SCRIPT;
		shell( cmd );
	}
}

main();
