/* eslint-disable no-sync */

'use strict';

// MODULES //

var remark = require( 'remark' );
var remarkUnlink = require( 'remark-unlink' );
var removeElements = require( './remove_elements.js' );
var extract = require( './extract.js' );


// MAIN //

/**
* Object containing functions for processing README sections.
*
* @private
* @constant
* @type {Object}
*/
var processors = {
	'intro': remark()
		.use( extract( 'intro' ) )
		.use( remarkUnlink )
		.use( removeElements )
		.processSync,
	'notes': remark()
		.use( extract( 'notes' ) )
		.use( remarkUnlink )
		.use( removeElements )
		.processSync,
	'usage': remark()
		.use( extract( 'usage' ) )
		.use( remarkUnlink )
		.use( removeElements )
		.processSync,
	'cli': remark()
		.use( extract( 'cli' ) )
		.use( remarkUnlink )
		.use( removeElements )
		.processSync,
	'references': remark()
		.use( extract( 'references' ) )
		.use( remarkUnlink )
		.use( removeElements )
		.processSync
};


// EXPORTS //

module.exports = processors;
