'use strict';

// MODULES //

var removePunctuation = require( '@stdlib/string/remove-punctuation' );
var lowercase = require( '@stdlib/string/lowercase' );
var trim = require( '@stdlib/string/trim' );
var replace = require( '@stdlib/string/replace' );
var processors = require( './processors.js' );


// MAIN //

/**
* Processes and returns the text of a README section.
*
* @private
* @param {string} file - file text
* @param {string} section - section name
* @returns {string} processed text
*/
function sectionText( file, section ) {
	var out = processors[ section ]( file ).contents;

	// Remove all comments:
	out = replace( out, /<!--[^->]+-->/g, '' );

	// Remove other special characters:
	out = replace( out, /[#*_\-=]/g, '' );

	// Convert any uppercase characters to lowercase:
	out = lowercase( out );

	// Remove punctuation:
	out = removePunctuation( out );

	// Remove leading and trailing whitespace:
	out = trim( out );

	return out;
}


// EXPORTS //

module.exports = sectionText;
