tape( 'the command-line interface prints a cumulative SLOC summary', opts, function test( t ) {
	var file;
	var opts;
	var cmd;

	file = __filename;
	cmd = [
		EXEC_PATH,
		'-e',
		'"process.stdin.isTTY = true; process.argv[ 2 ] = \''+file+'\'; process.argv[ 3 ] = \''+file+'\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'maxBuffer': 2000 * 1024
	};
	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var results;
		if ( error ) {
			t.fail( error.message );
		} else {
			results = stdout.toString().split( RE_EOL );
			t.strictEqual( results.length, 2, 'has expected number of newlines' );
			t.strictEqual( isJSON( results[ 0 ] ), true, 'prints JSON' );
			t.strictEqual( results[ 1 ], '', 'includes trailing newline' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

tape( 'if the `no-cumulative` flag is set, the command-line interface prints SLOC results as CSV', opts, function test( t ) {
	var file;
	var opts;
	var cmd;

	file = __filename;
	cmd = [
		EXEC_PATH,
		'-e',
		'"process.stdin.isTTY = true; process.argv[ 2 ] = \'--no-cumulative\'; process.argv[ 3 ] = \''+file+'\'; process.argv[ 4 ] = \''+file+'\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'maxBuffer': 2000 * 1024
	};
	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var results;
		var line;
		var i;
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );

			results = stdout.toString().split( RE_EOL );
			t.strictEqual( results.length, 4, 'returns expected number of results' );
			for ( i = 0; i < results.length-1; i++ ) {
				line = results[ i ];
				t.strictEqual( line.split( ',' ).length, 2, 'returns a line having two columns' );
			}
			t.strictEqual( results[i], '', 'includes ending newline' );
		}
		t.end();
	}
});

tape( 'the command-line interface supports providing a custom separator when used as a standard stream (regexp)', opts, function test( t ) {
	var file;
	var opts;
	var cmd;

	file = __filename;
	cmd = [
		'printf "'+file+'\t'+file+'"',
		'|',
		EXEC_PATH,
		'-e',
		'"process.stdin.isTTY = false; process.argv[ 2 ] = \'--split=/\\t/\'; require( \''+fpath+'\' );"'
	];
	opts = {
		'maxBuffer': 2000 * 1024
	};
	exec( cmd.join( ' ' ), opts, done );

	function done( error, stdout, stderr ) {
		var results;
		if ( error ) {
			t.fail( error.message );
		} else {
			results = stdout.toString().split( RE_EOL );
			t.strictEqual( results.length, 2, 'has expected number of newlines' );
			t.strictEqual( isJSON( results[ 0 ] ), true, 'prints JSON' );
			t.strictEqual( results[ 1 ], '', 'includes trailing newline' );
			t.strictEqual( stderr.toString(), '', 'does not print to `stderr`' );
		}
		t.end();
	}
});

