tape( 'the function returns an error to a provided callback if an error is encountered when reading files', function test( t ) {
	var doesNotExist = 'akfjdalfkjsdlfjdasfjadsfjdslfjldajfasdjfasjfjasdlfjasdlfj.js';
	analyze( [ doesNotExist ], clbk );

	function clbk( error ) {
		t.strictEqual( instanceOf( error, Error ), true, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an error to a provided callback if an error is encountered when reading files (options)', function test( t ) {
	var doesNotExist = 'akfjdalfkjsdlfjdasfjadsfjdslfjldajfasdjfasjfjasdlfjasdlfj.js';
	analyze( [ doesNotExist ], {}, clbk );

	function clbk( error ) {
		t.strictEqual( instanceOf( error, Error ), true, 'returns an error' );
		t.end();
	}
});

tape( 'by default, the function analyzes input files and returns a cumulative static summary to a provided callback', function test( t ) {
	var files = [ __filename, __filename ];
	analyze( files, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObject( o ), true, 'returns a plain object' );
			t.strictEqual( getKeys( o ).length > 0, true, 'has key-value pairs' );
		}
		t.end();
	}
});

tape( 'if the `cumulative` option is `true`, the function analyzes input files and returns a cumulative static summary to a provided callback', function test( t ) {
	var files;
	var opts;

	files = [ __filename, __filename ];
	opts = {
		'cumulative': true
	};
	analyze( files, opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObject( o ), true, 'returns a plain object' );
			t.strictEqual( getKeys( o ).length > 0, true, 'has key-value pairs' );
		}
		t.end();
	}
});

tape( 'if the `cumulative` option is `false`, the function analyzes input files and returns an array of program summaries to a provided callback', function test( t ) {
	var files;
	var opts;

	files = [ __filename, __filename ];
	opts = {
		'cumulative': false
	};
	analyze( files, opts, clbk );

	function clbk( error, o ) {
		var i;
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObjectArray( o ), true, 'returns an array of plain objects' );
			for ( i = 0; i < o.length; i++ ) {
				t.strictEqual( getKeys( o[ i ] ).length > 0, true, 'has key-value pairs' );
			}
		}
	}
	t.end();
});
