'use strict';

/**
* Perform a static summary analysis on a file glob.
*
* @module @stdlib/_tools/static-analysis/js/summarize-glob
*
* @example
* var analyze = require( '@stdlib/_tools/static-analysis/js/summarize-glob' );
*
* var opts = {
*     'dir': __dirname
* };
*
* analyze( opts, clbk );
*
* function clbk( error, results ) {
*     if ( error ) {
*         throw error;
*     }
*     if ( results ) {
*         console.log( JSON.stringify( results ) );
*     }
* }
*
* @example
* var analyze = require( '@stdlib/_tools/static-analysis/js/summarize-glob' ).sync;
*
* var opts = {
*     'dir': __dirname
* };
*
* var results = analyze( opts );
* if ( results instanceof Error ) {
*     throw results;
* }
* console.log( JSON.stringify( results ) );
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var main = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( main, 'sync', sync );


// EXPORTS //

module.exports = main;
