tape( 'the function returns an error to a provided callback if an error is encountered when searching for files', function test( t ) {
	var analyze = proxyquire( './../lib/main.js', {
		'glob': glob
	});

	analyze( clbk );

	function glob() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( new Error( 'beep' ) );
		}
	}

	function clbk( error ) {
		t.strictEqual( instanceOf( error, Error ), true, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an error to a provided callback if an error is encountered when searching for files (options)', function test( t ) {
	var analyze = proxyquire( './../lib/main.js', {
		'glob': glob
	});

	analyze( {}, clbk );

	function glob() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( new Error( 'beep' ) );
		}
	}

	function clbk( error ) {
		t.strictEqual( instanceOf( error, Error ), true, 'returns an error' );
		t.end();
	}
});

tape( 'the function returns an error to a provided callback if an error is encountered when performing static analysis', function test( t ) {
	var analyze;
	var opts;

	analyze = proxyquire( './../lib/main.js', {
		'@stdlib/_tools/static-analysis/js/summarize-file-list': mock
	});
	opts = {
		'dir': __dirname
	};
	analyze( opts, clbk );

	function mock() {
		var cb = arguments[ arguments.length-1 ];
		setTimeout( onTimeout, 0 );
		function onTimeout() {
			cb( new Error( 'beep' ) );
		}
	}

	function clbk( error ) {
		t.strictEqual( instanceOf( error, Error ), true, 'returns an error' );
		t.end();
	}
});

tape( 'if unable to resolve source files, the function returns `null` for analysis results', function test( t ) {
	var opts = {
		'dir': join( __dirname, 'fjadlfjadsfljsdfljksdafjsdlfjasl' ) // non-existent directory
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( o, null, 'returns expected value' );
		}
		t.end();
	}
});

tape( 'if unable to resolve source files and the `cumulative` option is `true`, the function returns `null` for analysis results', function test( t ) {
	var opts = {
		'cumulative': true,
		'dir': join( __dirname, 'fjadlfjadsfljsdfljksdafjsdlfjasl' ) // non-existent directory
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( o, null, 'returns expected value' );
		}
		t.end();
	}
});

tape( 'if unable to resolve source files and the `cumulative` option is `false`, the function returns an empty array for analysis results', function test( t ) {
	var opts = {
		'cumulative': false,
		'dir': join( __dirname, 'fjadlfjadsfljsdfljksdafjsdlfjasl' ) // non-existent directory
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isArray( o ), true, 'returns an array' );
			t.strictEqual( o.length, 0, 'returns an empty array' );
		}
		t.end();
	}
});

tape( 'by default, the function analyzes a file glob and returns a cumulative static summary to a provided callback', function test( t ) {
	var opts = {
		'dir': __dirname
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObject( o ), true, 'returns a plain object' );
			t.strictEqual( getKeys( o ).length > 0, true, 'has key-value pairs' );
		}
		t.end();
	}
});

tape( 'if the `cumulative` option is `true`, the function analyzes a file glob and returns a cumulative static summary to a provided callback', function test( t ) {
	var opts = {
		'dir': __dirname,
		'cumulative': true
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObject( o ), true, 'returns a plain object' );
			t.strictEqual( getKeys( o ).length > 0, true, 'has key-value pairs' );
		}
		t.end();
	}
});

tape( 'if the `cumulative` option is `false`, the function analyzes a file glob and returns an array of program summaries to a provided callback', function test( t ) {
	var opts = {
		'cumulative': false,
		'dir': __dirname
	};
	analyze( opts, clbk );

	function clbk( error, o ) {
		var i;
		if ( error ) {
			t.fail( error.message );
		} else {
			t.strictEqual( isPlainObjectArray( o ), true, 'returns an array of plain objects' );
			for ( i = 0; i < o.length; i++ ) {
				t.strictEqual( getKeys( o[ i ] ).length > 0, true, 'has key-value pairs' );
			}
		}
		t.end();
	}
});
