'use strict';

// MODULES //

var copy = require( '@stdlib/utils/copy' );
var replace = require( '@stdlib/string/replace' );
var defaults = require( './defaults.json' );
var validate = require( './validate.js' );
var url = require( './url.js' );


// VARIABLES //

// Match any string ending with `/`:
var DANGLING_SLASH = /\/$/;

// Match any string beginning with `./`:
var FILEPATH_PREFIX = /^\.\//;


// MAIN //

/**
* Returns a jsDelivr URL for a file hosted in a public GitHub repository.
*
* @param {Options} options - function options
* @param {string} options.slug - public GitHub repository slug
* @param {string} options.file - file path
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {TypeError} must provide a repository slug
* @throws {TypeError} must provide a file path
* @returns {string} jsDelivr URL
*
* @example
* var out = jsdelivr({
*     'slug': 'stdlib-js/stdlib/develop',
*     'file': 'README.md'
* });
* // returns 'https://cdn.jsdelivr.net/gh/stdlib-js/stdlib@develop/README.md'
*/
function jsdelivr( options ) {
	var opts;
	var err;

	opts = copy( defaults );
	err = validate( opts, options );
	if ( err ) {
		throw err;
	}
	opts.slug = replace( opts.slug, DANGLING_SLASH, '' );
	opts.file = replace( opts.file, FILEPATH_PREFIX, '' );
	return url( opts );
}


// EXPORTS //

module.exports = jsdelivr;
