'use strict';

// MODULES //

var tape = require( 'tape' );
var toAccessorArray = require( '@stdlib/array/base/to-accessor-array' );
var cuany = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof cuany, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function cumulatively tests elements of array to be truthy (generic)', function test( t ) {
	var expected;
	var actual;
	var x;

	x = [ false, false, true, false, false ];

	actual = cuany( x );
	expected = [ false, false, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [false, false, false, false, false];
	actual = cuany( x );
	expected = [false, false, false, false, false];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ true, true, true, true, true ];
	actual = cuany( x );
	expected = [ true, true, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ false, true, false ];
	actual = cuany( x );
	expected = [ false, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = [ true, false, false, false, false ];
	actual = cuany( x );
	expected = [ true, true, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();
});

tape( 'the function cumulatively tests elements of accessor array to be truthy (accessor)', function test( t ) {
	var expected;
	var actual;
	var x;

	x = toAccessorArray( [ false, false, true, false, false ] );

	actual = cuany( x );
	expected = [ false, false, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [false, false, false, false, false] );
	actual = cuany( x );
	expected = [false, false, false, false, false];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ true, true, true, true, true ] );
	actual = cuany( x );
	expected = [ true, true, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ false, true, false ] );
	actual = cuany( x );
	expected = [ false, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	x = toAccessorArray( [ true, false, false, false, false ] );
	actual = cuany( x );
	expected = [ true, true, true, true, true ];
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();
});
