'use strict';

// MODULES //

var tape = require( 'tape' );
var filled = require( '@stdlib/array/filled' );
var get = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an array element', function test( t ) {
	var arr;
	var v;
	var i;

	arr = [ 1, 2, 3, 4 ];
	for ( i = 0; i < arr.length; i++ ) {
		t.strictEqual( get( arr, i ), arr[ i ], 'returns expected value' );
	}
	arr = filled( 1.0, 10, 'float64' );
	v = get( arr, 2 );
	t.strictEqual( v, 1.0, 'returns expected value' );

	arr = filled( 2.0, 10, 'float32' );
	v = get( arr, 2 );
	t.strictEqual( v, 2.0, 'returns expected value' );

	arr = filled( 3, 10, 'int32' );
	v = get( arr, 2 );
	t.strictEqual( v, 3, 'returns expected value' );

	arr = filled( 4, 10, 'int16' );
	v = get( arr, 2 );
	t.strictEqual( v, 4, 'returns expected value' );

	arr = filled( 5, 10, 'int8' );
	v = get( arr, 2 );
	t.strictEqual( v, 5, 'returns expected value' );

	arr = filled( 6, 10, 'uint32' );
	v = get( arr, 2 );
	t.strictEqual( v, 6, 'returns expected value' );

	arr = filled( 7, 10, 'uint16' );
	v = get( arr, 2 );
	t.strictEqual( v, 7, 'returns expected value' );

	arr = filled( 8, 10, 'uint8' );
	v = get( arr, 2 );
	t.strictEqual( v, 8, 'returns expected value' );

	arr = filled( 9, 10, 'uint8c' );
	v = get( arr, 2 );
	t.strictEqual( v, 9, 'returns expected value' );

	t.end();
});

tape( 'the function does not perform bounds checks', function test( t ) {
	var arr;
	var v;

	arr = [ 1, 2, 3, 4 ];
	v = get( arr, arr.length + 10 );

	t.strictEqual( v, void 0, 'returns expected value' );
	t.end();
});
