'use strict';

// MODULES //

var tape = require( 'tape' );
var Complex64Array = require( '@stdlib/array/complex64' );
var toAccessorArray = require( '@stdlib/array/base/to-accessor-array' );
var reinterpret = require( '@stdlib/strided/base/reinterpret-complex' );
var Int32Array = require( '@stdlib/array/int32' );
var zeros = require( '@stdlib/array/zeros' );
var mskfilterMap = require( './../lib' ).assign;


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof mskfilterMap, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function filters array elements (generic)', function test( t ) {
	var expected;
	var actual;
	var mask;
	var out;
	var x;

	x = [ 1, 2, 3, 4 ];

	mask = [ 0, 1, 0, 1 ];
	out = zeros( 2, 'generic' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = [ 4, 16 ];

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 0, 0, 0 ];
	out = zeros( 0, 'generic' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = [];

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 0, 0, 1 ];
	out = zeros( 1, 'generic' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = [ 16 ];

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 1, 1, 1, 1 ];
	out = zeros( 4, 'generic' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = [ 1, 4, 9, 16 ];

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 1, 0, 1 ];
	out = zeros( 4, 'generic' );
	actual = mskfilterMap( x, mask, out, -2, out.length-1, clbk );
	expected = [ 0, 16, 0, 4 ];

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();

	function clbk( val ) {
		return val * val;
	}
});

tape( 'the function filters array elements (real typed array)', function test( t ) {
	var expected;
	var actual;
	var mask;
	var out;
	var x;

	x = new Int32Array( [ 1, 2, 3, 4 ] );

	mask = [ 0, 1, 0, 1 ];
	out = zeros( 2, 'int32' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = new Int32Array( [ 4, 16 ] );

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 0, 0, 0 ];
	out = zeros( 0, 'int32' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = new Int32Array( [] );

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 0, 0, 1 ];
	out = zeros( 1, 'int32' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = new Int32Array( [ 16 ] );

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 1, 1, 1, 1 ];
	out = zeros( 4, 'int32' );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = new Int32Array( [ 1, 4, 9, 16 ] );

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	mask = [ 0, 1, 0, 1 ];
	out = zeros( 4, 'int32' );
	actual = mskfilterMap( x, mask, out, -2, out.length-1, clbk );
	expected = new Int32Array( [ 0, 16, 0, 4 ] );

	t.strictEqual( actual, out, 'returns expected value' );
	t.deepEqual( actual, expected, 'returns expected value' );

	t.end();

	function clbk( val ) {
		return val * val;
	}
});

tape( 'the function filters array elements (complex typed array)', function test( t ) {
	var expected;
	var actual;
	var mask;
	var out;
	var x;

	x = reinterpret(new Complex64Array( [ 1.0, 2.0, 3.0, 4.0 ] ) );

	mask = [ 1, 0 ];
	out = reinterpret( zeros( 1, 'complex64' ) );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = reinterpret( new Complex64Array( [ 1.0, 4.0, 25.0, 36.0 ] ) );

	t.strictEqual( actual, out, 'returns expected value' );
	isEqual( actual, expected );

	mask = [ 0, 0 ];
	out = reinterpret( zeros( 0, 'complex64' ) );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = reinterpret( new Complex64Array( [] ) );

	t.strictEqual( actual, out, 'returns expected value' );
	isEqual( actual, expected );

	mask = [ 0, 1 ];
	out = reinterpret( zeros( 1, 'complex64' ) );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = reinterpret( new Complex64Array( [ 9.0, 16.0 ] ) );

	t.strictEqual( actual, out, 'returns expected value' );
	isEqual( actual, expected );

	mask = [ 1, 1 ];
	out = reinterpret( zeros( 3, 'complex64' ) );
	actual = mskfilterMap( x, mask, out, 1, 0, clbk );
	expected = reinterpret( new Complex64Array( [ 1.0, 4.0, 9.0, 16.0 ] ) );

	t.strictEqual( actual, out, 'returns expected value' );
	isEqual( actual, expected );

	mask = [ 1, 0 ];
	out = reinterpret( zeros( 3, 'complex64' ) );
	actual = mskfilterMap( x, mask, out, -2, out.length-1, clbk );
	expected = reinterpret( new Complex64Array( [ 0.0, 0.0, 1.0, 4.0 ] ) );

	t.strictEqual( actual, out, 'returns expected value' );
	isEqual( actual, expected );

	t.end();

	function isEqual( actual, expected ) {
		var i;
		for ( i = 0; i < expected.length; i++ ) {
			t.strictEqual( actual.get( i ), expected[ i ], 'returns expected value' );
		}
	}

	function clbk( val ) {
		return val * val;
	}
});

