'use strict';

// MODULES //

var isArrayLikeObject = require( '@stdlib/assert/is-array-like-object' );
var getType = require( '@stdlib/array/dtype' );
var ctors = require( '@stdlib/array/ctors' );


// MAIN //

/**
* Converts an array to the same data type as a second input array.
*
* @param {ArrayLikeObject} x - array to convert
* @param {(Array|TypedArray)} y - array having the desired output data type
* @throws {TypeError} first argument must be an array-like object
* @throws {TypeError} second argument must have a recognized data type
* @returns {(Array|TypedArray)} output array
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
*
* var y = new Float64Array( 0 );
*
* var x = [ 1.0, 2.0, 3.0, 4.0 ];
* var out = convertSame( x, y );
* // returns <Float64Array>[ 1.0, 2.0, 3.0, 4.0 ]
*/
function convertSame( x, y ) {
	var dtype;
	var ctor;
	var out;
	var len;
	var i;
	if ( !isArrayLikeObject( x ) ) {
		throw new TypeError( 'invalid input argument. First argument must be an array-like object. Value: `' + x + '`.' );
	}
	dtype = getType( y );
	ctor = ctors( dtype );
	if ( ctor === null ) {
		throw new TypeError( 'invalid input argument. Second argument must have a recognized data type. Value: `' + dtype + '`.' );
	}
	len = x.length;
	if ( dtype === 'generic' ) {
		out = [];
		for ( i = 0; i < len; i++ ) {
			out.push( x[ i ] ); // ensure "fast" elements
		}
		return out;
	}
	out = new ctor( len ); // eslint-disable-line no-new-cap
	for ( i = 0; i < len; i++ ) {
		out[ i ] = x[ i ];
	}
	return out;
}


// EXPORTS //

module.exports = convertSame;
