'use strict';

// MODULES //

var isObjectLike = require( '@stdlib/assert/is-object-like' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );


// MAIN //

/**
* Returns a boolean indicating whether an object has a nested property.
*
* @private
* @param {ObjectLike} obj - input object
* @param {Array} props - list of properties defining a key path
* @returns {boolean} boolean indicating whether an object has a nested property
*/
function deepHasOwn( obj, props ) {
	var len = props.length;
	var i;
	if ( len === 0 ) {
		return false;
	}
	for ( i = 0; i < len; i++ ) {
		if ( isObjectLike( obj ) && hasOwnProp( obj, props[i] ) ) {
			obj = obj[ props[ i ] ];
		} else {
			return false;
		}
	}
	return true;
} // end FUNCTION deepHasOwn()


// EXPORTS //

module.exports = deepHasOwn;
