'use strict';

// MODULES //

var tape = require( 'tape' );
var isConstantcase = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isConstantcase, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a string having constantcase format', function test( t ) {
	var values;
	var i;

	values = [
		'BEEP_BOOP',
		'BEEP_BOOP',
		'BEEP_BOOP_BEEP_BOOP',
		'beep-boop',
		'BEEP-BOOP',
		'BEeP-bOoP',
		'beep-BEEP',
		'beep-BEEP-boop',
		'beep-beep-boop',
		'beep-beep-BEEP-boop',
		'beep-beep-BEEP-boop-BEEP-boop',
		'beep-Beep-boop-boop',
		'beep-Beep-BEEP-boop-BEEP-BOOP',
		'bEEP-bEEP-bOOP-bOOP',
		'bEEP-bEEP-bOOP-bOOP-beep-BEEP-boop',
		'BEEP-boop'
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isConstantcase( values[i] ), true, 'returns true when provided '+values[i] );
	}
	t.end();
});

tape( 'the function returns `false` if provided a string not having constantcase format', function test( t ) {
	var values;
	var i;

	values = [
		'beep',
		'beep Boop',
		'beep boop',
		'Beep Boop',
		'Beep boop',
		'Beep Boop beep boop',
		'beep-boop-beep',
		'beep-beep-boop-boop',
		'beep-Beep-BEEP-boop-BEEP-BOOP',
		'bEEP-bEEP-bOOP-bOOP',
		'bEEP-bEEP-bOOP-bOOP-beep-BEEP-boop',
		'beep-boop-boop',
		'Beep-boop-boop',
		'boop-beep',
		'boop-beep-BEEP',
		'beep-BEEP-boop',
		'BEEP-boop'
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isConstantcase( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});

tape( 'the function returns `false` if not provided a string', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		null,
		undefined,
		true,
		false,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isConstantcase( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});

