'use strict';

// MODULES //

var tape = require( 'tape' );
var isDurationString = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isDurationString, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a duration string', function test( t ) {
	var values;
	var i;

	values = [
		'1s',
		'2m',
		'3h',
		'1d',
		'1w',
		'2M',
		'3y',
		'1s2m',
		'1h2m',
		'1d2h',
		'1w2d',
		'1y2M',
		'1y2w3d4h5m6s',
		'1Y2W3D4H5M6S'
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isDurationString( values[i] ), true, 'returns true when provided '+values[i] );
	}
	t.end();
});

tape( 'the function returns `false` if not provided a duration string', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		'beep',
		'1',
		'1.2',
		new Date(),
		NaN,
		true,
		false,
		null,
		undefined,
		function noop() {},
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isDurationString( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});

